CREATE OR REPLACE FUNCTION obter_se_mesmo_setor_exclusivo ( cd_pessoa_fisica_p bigint, dt_agenda_p timestamp, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w			varchar(1);
cd_setor_excl_novo_w		bigint;
nr_seq_agenda_w			    agenda_consulta.nr_sequencia%type;

C01 CURSOR FOR 		--  TODOS SETORES EXCLUSIVOS DO PACIENTE NO DIA
	SELECT	a.nr_sequencia
	from	agenda_consulta a,
		agenda b
	where	a.cd_agenda = b.cd_agenda
	and	b.cd_tipo_agenda = 3
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_status_agenda not in ('C','F','I')
	and	(b.cd_setor_exclusivo IS NOT NULL AND b.cd_setor_exclusivo::text <> '')
	and	b.cd_setor_exclusivo <> cd_setor_excl_novo_w
	and	trunc(a.dt_agenda) = trunc(dt_agenda_p);			


BEGIN

select	max(b.cd_setor_exclusivo)	-- SETOR EXCLUSIVO DA NOVA AGENDA
into STRICT	cd_setor_excl_novo_w
from	agenda b
where	b.cd_agenda = cd_agenda_p;

open C01;
loop
fetch C01 into	
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_agenda_w := nr_seq_agenda_w;
	end;
end loop;
close C01;

if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
	ds_retorno_w := 'N'; --  QUANDO POSSUI SETOR DIFERENTE
else
	ds_retorno_w := 'S';
end if;
	
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
