SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_meta_liberada ( nr_atendimento_p bigint, nr_seq_meta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w    		varchar(1) := 'S';
nm_usuario_w			varchar(15);
cd_perfil_w				integer;
cd_estabelecimento_w	smallint;
qt_reg_w				bigint;
qt_reg2_w				bigint;
cd_setor_atendimento_w	integer;
ie_profissional_w		varchar(15);
ie_tipo_atendimento_w	varchar(15);


BEGIN


Select   count(*)
into STRICT	 qt_reg_w
from	 tof_meta_restrincao
where	 nr_seq_meta = 	nr_seq_meta_p;

if (qt_reg_w > 0 ) then

	nm_usuario_w 		:= wheb_usuario_pck.get_nm_usuario;
	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
	cd_perfil_w		:= obter_perfil_Ativo;
	cd_setor_atendimento_w 	:= obter_setor_Atendimento(nr_atendimento_p);
	ie_tipo_atendimento_w := Obter_Tipo_Atendimento(nr_atendimento_p);
	
	Select  max(ie_profissional)
	into STRICT	ie_profissional_w
	from	usuario
	where 	nm_usuario = nm_usuario_w;
	
	
	Select  count(*)
	into STRICT	qt_reg2_w
	from	tof_meta_restrincao
	where	nr_seq_meta = 	nr_seq_meta_p
	and coalesce(cd_estabelecimento,cd_estabelecimento_w,0) =  coalesce(cd_estabelecimento_w,0)
	and coalesce(cd_setor_atendimento,cd_setor_atendimento_w,0) =  coalesce(cd_setor_atendimento_w,0)
	and coalesce(cd_perfil,cd_perfil_w,0) =  coalesce(cd_perfil_w,0)
	and coalesce(ie_profissional,ie_profissional_w,'XPT') =  coalesce(ie_profissional_w,'XPT')
	and coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w,'0') =  coalesce(ie_tipo_atendimento_w,'0');
	
	if (qt_reg2_w = 0 ) then
	
		ie_permite_w := 'N';
	
	end if;
	

end if;
	

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
