SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_modalidade_lib ( ie_modalidade_trat_p text, nr_seq_loco_regional_p bigint) RETURNS varchar AS $body$
DECLARE

cd_topografia_w		varchar(10);
cd_estadiamento_w	varchar(10);
ie_retorno_w		varchar(10)	:= 'S';
qt_reg_w		bigint;
CD_ESTADIO_OUTRO_W	varchar(10);

BEGIN

select	count(*)
into STRICT	qt_reg_w
from	REGRA_MODALIDADE_ONC;


if (nr_seq_loco_regional_p IS NOT NULL AND nr_seq_loco_regional_p::text <> '') and (qt_reg_w	> 0)then
	select	max(cd_topografia),
		max(cd_estadiamento),
		max(CD_ESTADIO_OUTRO)
	into STRICT	cd_topografia_w,
		cd_estadiamento_w,
		CD_ESTADIO_OUTRO_W
	from	can_loco_regional
	where	nr_sequencia	= nr_seq_loco_regional_p;

	if (cd_topografia_w IS NOT NULL AND cd_topografia_w::text <> '') and (cd_estadiamento_w IS NOT NULL AND cd_estadiamento_w::text <> '') and (coalesce(CD_ESTADIO_OUTRO_W::text, '') = '')then
		select	count(*)
		into STRICT	qt_reg_w
		from	REGRA_MODALIDADE_ONC
		where	cd_topografia	= cd_topografia_w
		and	cd_estadiamento	= cd_estadiamento_w;

		if (qt_reg_w		> 0) then
			select	count(*)
			into STRICT	qt_reg_w
			from	REGRA_MODALIDADE_ONC
			where	cd_topografia	= cd_topografia_w
			and	cd_estadiamento	= cd_estadiamento_w
			and	ie_modalidade_trat	= ie_modalidade_trat_p;

			if (qt_reg_w	= 0) then
				ie_retorno_w	:= 'N';
			end if;
		end if;
	end if;


	if (CD_ESTADIO_OUTRO_W IS NOT NULL AND CD_ESTADIO_OUTRO_W::text <> '') and (cd_topografia_w IS NOT NULL AND cd_topografia_w::text <> '') then
		select	count(*)
		into STRICT	qt_reg_w
		from	REGRA_MODALIDADE_ONC
		where	cd_topografia	= cd_topografia_w
		and	cd_estadiamento	= CD_ESTADIO_OUTRO_W;

		if (qt_reg_w		> 0) then
			select	count(*)
			into STRICT	qt_reg_w
			from	REGRA_MODALIDADE_ONC
			where	cd_topografia	= cd_topografia_w
			and	cd_estadiamento	= CD_ESTADIO_OUTRO_W
			and	ie_modalidade_trat	= ie_modalidade_trat_p;

			if (qt_reg_w	= 0) then
				ie_retorno_w	:= 'N';
			end if;
		end if;
	end if;

end if;



return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
