CREATE OR REPLACE FUNCTION obter_se_mod_finali ( ie_finalidade_p text, ie_modalidade_trat_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;

BEGIN



if (ie_modalidade_trat_p IS NOT NULL AND ie_modalidade_trat_p::text <> '') and (ie_finalidade_p IS NOT NULL AND ie_finalidade_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	REGRA_MODA_FINALIDADE
	where	ie_finalidade	= ie_finalidade_p;

	if (qt_reg_w	> 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	REGRA_MODA_FINALIDADE
		where	ie_finalidade	= ie_finalidade_p
		and		coalesce(ie_modalidade_trat,ie_modalidade_trat_p)	= ie_modalidade_trat_p;

		if (qt_reg_w	= 0) then
			return 'N';
		end if;

	end if;


end if;

return	'S';

end;
$body$
