CREATE OR REPLACE FUNCTION obter_se_mod_lib_perfil (ie_modalidade_trat_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(5) := 'S';
cd_perfil_w		bigint;
cd_perfil_regra_w	bigint;
qt_reg_w		bigint;

C01 CURSOR FOR
	SELECT	cd_perfil
	from	regra_modalidade_perfil
	where	ie_modalidade_trat = ie_modalidade_trat_p;


BEGIN

select 	count(*)
into STRICT	qt_reg_w
from	regra_modalidade_perfil
where	ie_modalidade_trat = ie_modalidade_trat_p;

if (qt_reg_w > 0) then
	ds_retorno_w := 'N';
	cd_perfil_w := obter_perfil_ativo;
	open C01;
	loop
	fetch C01 into
		cd_perfil_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (cd_perfil_w = cd_perfil_regra_w) then
			ds_retorno_w := 'S';
		end if;
		end;
	end loop;
	close C01;

end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
