SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mod_sae_lib_vis (nr_seq_pe_sae_model_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_w		bigint;
ie_tipo_evolucao_w	varchar(4);
cd_estabelecimento_w	pe_sae_modelo_perfil.cd_estabelecimento%type := 0;
				

BEGIN

select	coalesce(max(ie_tipo_evolucao),'XPTO')
into STRICT	ie_tipo_evolucao_w
from	usuario
where	nm_usuario =  wheb_usuario_pck.get_nm_usuario;

select	count(*)
into STRICT	qt_itens_w
from	pe_sae_modelo_perfil
where	nr_seq_pe_sae_model		=	nr_seq_pe_sae_model_p
and	coalesce(IE_VISUALIZACAO_EXCL,'N')	= 'S';

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

if (qt_itens_w = 0) then
	return 'S';
else
	select	count(*)
	into STRICT	qt_itens_w
	from	pe_sae_modelo_perfil
	where	nr_seq_pe_sae_model		= nr_seq_pe_sae_model_p
	and	cd_perfil			= obter_perfil_ativo
	and	coalesce(ie_tipo_evolucao,ie_tipo_evolucao_w)	= ie_tipo_evolucao_w
	and	coalesce(IE_VISUALIZACAO_EXCL,'N')	= 'S'
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0);
	
	if (qt_itens_w > 0) then	
		return 'S';
	else
		return 'N';
	end if;	
end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
