CREATE OR REPLACE FUNCTION obter_se_mostra_adic (ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(5);

BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then
	select	coalesce(max(ie_mostrar_adic),'S')
	into STRICT	ie_retorno_w
	from 	rep_itens_adicionais
	where	ie_tipo_item = ie_tipo_item_p;
	if (coalesce(ie_retorno_w::text, '') = '') or (ie_retorno_w = '') then
		ie_retorno_w := 'S';
	end if;
end if;

return coalesce(ie_retorno_w,'S');

end;
$body$
