CREATE OR REPLACE FUNCTION obter_se_mostra_alerta_classif (cd_funcao_ativa_p bigint, nr_seq_classif_pessoa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostra_alerta_w	varchar(10);

c01 CURSOR FOR
SELECT	ie_mostra_alerta
from	regra_alerta_classif_pf
where	cd_funcao	= cd_funcao_ativa_p
and	nr_seq_classif	= nr_seq_classif_pessoa_p
and	ie_situacao	= 'A';


BEGIN

ie_mostra_alerta_w	:= 'S';

open c01;
loop
fetch c01 into
	ie_mostra_alerta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_mostra_alerta_w	:= ie_mostra_alerta_w;
end loop;
close c01;

return ie_mostra_alerta_w;

end;
$body$
