SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_comp_agente ( nr_seq_agente_p bigint ) RETURNS bigint AS $body$
DECLARE



qt_medicamento_w	bigint := 0;
qt_componente_w		bigint := 0;
nr_sequencia_w		agente_anest_material.nr_sequencia%type;



C01 CURSOR FOR
	SELECT	nr_sequencia
	from	agente_anest_material
	where	nr_seq_agente = nr_seq_agente_p;


BEGIN

OPEN C01;
	LOOP
		FETCH C01 INTO 	
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		
			select  count(*)
			into STRICT	qt_componente_w
			from    agente_anest_componente
			where   nr_seq_agente = nr_sequencia_w;
			
			qt_medicamento_w := qt_medicamento_w + qt_componente_w;
		
	END LOOP;
CLOSE C01;

return	qt_medicamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
