CREATE OR REPLACE FUNCTION obter_se_mostra_cot_usuario ( nr_cot_compra_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ie_mostra_w			varchar(1) := 'N';
ie_possui_solic_w			varchar(1) := 'N';
nr_solic_compra_w			bigint;
cd_centro_custo_w			bigint;
cd_local_estoque_w		smallint;

c01 CURSOR FOR 
SELECT	distinct 
	nr_solic_compra 
from (	SELECT	a.nr_solic_compra 
	from	cot_compra_item a 
	where	a.nr_cot_compra = nr_cot_compra_p 
	and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '') 
	
union
 
	select	b.nr_solic_compra 
	from	cot_compra_item a, 
		cot_compra_solic_agrup b 
	where	a.nr_cot_compra = b.nr_cot_compra 
	and	a.nr_item_cot_compra = b.nr_item_cot_compra 
	and	a.nr_cot_compra = nr_cot_compra_p 
	and	(b.nr_solic_compra IS NOT NULL AND b.nr_solic_compra::text <> '')) alias2;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	ie_possui_solic_w	:= 'S';
 
	if (ie_mostra_w = 'N') then 
		select	cd_centro_custo, 
			cd_local_estoque 
		into STRICT	cd_centro_custo_w, 
			cd_local_estoque_w 
		from	solic_compra 
		where	nr_solic_compra	= nr_solic_compra_w;
		 
		if (ie_mostra_w = 'N') and (cd_centro_custo_w IS NOT NULL AND cd_centro_custo_w::text <> '') and (obter_se_cc_lib_usuario(nm_usuario_p, cd_centro_custo_w) = 'S') then 
			ie_mostra_w := 'S';
		end if;
		 
		if (ie_mostra_w = 'N') and (cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') and (obter_se_local_lib_usuario(nm_usuario_p, cd_local_estoque_w) = 'S') then 
			ie_mostra_w := 'S';
		end if;
	end if;
	end;
end loop;
close C01;
 
/*tratamento para mostrar a cotação caso ela não tenha nenhuma solicitação de compras. Ou seja, a cotação de compras não é de nenhum centro de custo e de nenhum local*/
 
if (ie_possui_solic_w = 'N') then 
	ie_mostra_w := 'S';
end if;
 
return	ie_mostra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
