CREATE OR REPLACE FUNCTION obter_se_mostra_derivado ( nr_seq_derivado_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(1) := 'S';
cont_w		bigint;

c01 CURSOR FOR
SELECT	ie_mostrar
from	regra_apres_derivado
where	nr_seq_derivado	= nr_seq_derivado_p
and	coalesce(cd_perfil, cd_perfil_p)	= cd_perfil_p
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
and	coalesce(cd_estab, cd_estabelecimento_p)	= cd_estabelecimento_p
order by coalesce(cd_perfil, 0),
	coalesce(cd_setor_atendimento, 0),
	coalesce(cd_estab, 0);


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_apres_derivado
where	nr_seq_derivado	= nr_seq_derivado_p;

if (cont_w > 0) then

	ie_mostrar_w := 'N';

	open C01;
	loop
	fetch C01 into
		ie_mostrar_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_mostrar_w	:= ie_mostrar_w;
	end loop;
	close C01;
end if;

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
