CREATE OR REPLACE FUNCTION obter_se_mostra_diluicao ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_agrupamento_w	bigint;
nr_sequencia_w		bigint;
result_w		varchar(2000);


BEGIN 
 
select	max(nr_agrupamento) 
into STRICT	nr_agrupamento_w 
from	prescr_material 
where	nr_prescricao	= nr_prescricao_p 
and		nr_sequencia	= nr_sequencia_p;
 
select	max(nr_sequencia) 
into STRICT	nr_sequencia_w 
from	prescr_material 
where	nr_prescricao	= nr_prescricao_p 
and	nr_agrupamento	= nr_agrupamento_w 
and	ie_agrupador	not in (3,7,9);
 
if (ie_opcao_p	= 'A') then 
	result_w	:= nr_sequencia_w;
else 
	select	max(obter_diluicao_medic(nr_sequencia, nr_prescricao_p)) 
	into STRICT	result_w 
	from	prescr_material 
	where	nr_prescricao	= nr_prescricao_p 
	and	nr_agrupamento	= nr_agrupamento_w 
	and	ie_agrupador	not in (3,7,9) 
	and	(obter_diluicao_medic(nr_sequencia, nr_prescricao_p) IS NOT NULL AND (obter_diluicao_medic(nr_sequencia, nr_prescricao_p))::text <> '');
end if;
 
return	result_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
