SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_escala ( nm_usuario_p text, nr_seq_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_perfil_w	varchar(1);
ds_retorno_w		varchar(1) := 'S';
qt_perfil_w		bigint;


BEGIN

select	ie_exige_perfil
into STRICT	ie_exige_perfil_w
from	escala
where	nr_sequencia	= nr_seq_escala_p;

if (ie_exige_perfil_w <> 'S') then
	ds_retorno_w	:= 'S';
else
	select	count(*)
	into STRICT	qt_perfil_w
	from	usuario_perfil b,
		escala_perfil a
	where	a.cd_perfil	= b.cd_perfil
	and	a.nr_seq_escala	= nr_seq_escala_p
	and	b.nm_usuario	= nm_usuario_p;

	if (qt_perfil_w = 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
