CREATE OR REPLACE FUNCTION obter_se_mostra_inspecao_usu ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_mostra_w			varchar(1) := 'N';
ie_possui_ordem_w			varchar(1) := 'N';
nr_ordem_compra_w		bigint;
cd_centro_custo_w			bigint;
cd_local_estoque_w		smallint;

c01 CURSOR FOR
SELECT	distinct
	a.nr_ordem_compra
from	inspecao_recebimento a
where	a.nr_sequencia		= nr_sequencia_p
and	(a.nr_ordem_compra IS NOT NULL AND a.nr_ordem_compra::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_possui_ordem_w	:= 'S';

	if (ie_mostra_w = 'N') and (obter_se_mostra_ordem_usuario(nr_ordem_compra_w, nm_usuario_p) = 'S') then
		ie_mostra_w := 'S';
	end if;

	end;
end loop;
close C01;

/*tratamento para mostrar a inspecao caso ela não tenha nenhuma ordem de compras. Ou seja, quando a inspecao não é de nenhuma ordem*/

if (ie_possui_ordem_w = 'N') then
	ie_mostra_w := 'S';
end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
