SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_lote (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_parametro_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

			
nr_lote_w		bigint;
ie_status_atend_w 	smallint;
ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
/* Opcao	1 - NUMERO LOTE
	2 - DS ENTIDADE
*/
BEGIN

select 	max(ie_status_atend),
	coalesce(MAX(nr_seq_lote_externo),0)
into STRICT	ie_status_atend_w,
	nr_lote_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia = nr_seq_prescr_p;

if (ie_status_atend_w in (40,45)) and (ie_parametro_p = 'N') and (ie_opcao_p = 1) then	
	return 0;
elsif (ie_status_atend_w in (40,45)) and (ie_parametro_p = 'N') and (ie_opcao_p = 2) then
	return '';
elsif (ie_opcao_p = 2) then
	select 	b.ds_razao_social
	into STRICT	ds_razao_social_w
	from	pessoa_juridica b,
		lab_lote_externo a
	where 	a.cd_cgc = b.cd_cgc
	and 	a.nr_sequencia = nr_lote_w;
	
	return ds_razao_social_w;

elsif (ie_opcao_p = 1) then	
	return nr_lote_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
