SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_material ( nr_sequencia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


nr_seq_material_w	bigint;
nr_seq_exame_w	bigint;
qt_material_w		bigint;
ie_retorno_w		varchar(1);


BEGIN

if (ie_opcao_p = 'E') then
	begin
	select	coalesce(max(nr_seq_material),0)
	into STRICT	nr_seq_material_w
	from 	exame_lab_rotina
	where	nr_sequencia	=	nr_sequencia_p;

	select	coalesce(max(nr_seq_exame),0)
	into STRICT	nr_seq_exame_w
	from 	exame_lab_rotina
	where	nr_sequencia	=	nr_sequencia_p;

	select	count(*)
	into STRICT	qt_material_w
	from	material_exame_lab b,
		exame_lab_material a
	where 	a.nr_seq_material 	= 	b.nr_sequencia
	and	a.nr_seq_exame    	= 	nr_seq_exame_w
	and	a.ie_situacao		= 	'A';

	if (nr_seq_material_w = 0) and (qt_material_w > 1) then
		ie_retorno_w	:=  'S';
	else
		ie_retorno_w	:= 'N';
	end if;
	end;
end if;

if (ie_opcao_p = 'L') then
	begin
	select	count(*)
	into STRICT	qt_material_w
	from	material_exame_lab b,
		exame_lab_material a
	where 	a.nr_seq_material 	= 	b.nr_sequencia
	and	a.nr_seq_exame    	= 	nr_sequencia_p
	and	a.ie_situacao		= 	'A';

	if (qt_material_w > 1) then
		ie_retorno_w	:=  'S';
	else
		ie_retorno_w	:= 'N';
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
