CREATE OR REPLACE FUNCTION obter_se_mostra_medic_espec ( nr_seq_medic_p bigint, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE


qt_total_w	integer;
ie_mostra_w	varchar(1);


BEGIN

/* Verifica se tem alguma especialidade informada */

begin
select	count(*)
into STRICT	qt_total_w
from	med_medic_padrao_espec
where	nr_seq_medic		= nr_seq_medic_p;
exception
	when others then
	qt_total_w 		:= 0;
end;

/* Caso nÃ£o tenha nenhuma informada, sai liberando o medicamento para o medico */

if (qt_total_w = 0) then
	qt_total_w		:= 1;
else
	begin
	select	count(*)
	into STRICT	qt_total_w
	from	med_medic_padrao_espec
	where	nr_seq_medic		= nr_seq_medic_p
	and	cd_especialidade	= cd_especialidade_p;
	exception
		when others then
		qt_total_w 		:= 0;
	end;
end if;

if (qt_total_w > 0) then
	ie_mostra_w		:= 'S';
else
	ie_mostra_w		:= 'N';
end if;

return ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
