CREATE OR REPLACE FUNCTION obter_se_mostra_medic_item ( ie_item_prescr_p text, cd_material_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w				bigint;
ie_retorno_w				varchar(1)	:= 'S';
cd_grupo_material_w			smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
nr_seq_familia_w		bigint;
cd_unid_med_solic_w		varchar(50);
ie_tipo_material_w		varchar(3);

c01 CURSOR FOR
SELECT	coalesce(ie_exibir, 'S')
from	rep_regra_material
where	ie_item_prescr						= ie_item_prescr_p
and	coalesce(cd_material,coalesce(cd_material_p,0))			= coalesce(cd_material_p,0)
and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))	= coalesce(cd_grupo_material_w,0)
and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
and	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))	= coalesce(cd_classe_material_w,0)
and	coalesce(nr_seq_familia,coalesce(nr_seq_familia_w,0))		= coalesce(nr_seq_familia_w,0)
and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
and	coalesce(cd_perfil, coalesce(obter_perfil_ativo,0))		= coalesce(obter_perfil_ativo,0)
and	coalesce(ie_tipo_material, coalesce(ie_tipo_material_w,'0'))		= coalesce(ie_tipo_material_w,'0')
and	coalesce(cd_unidade_medida_solic, coalesce(cd_unid_med_solic_w,'XPTO'))	= coalesce(cd_unid_med_solic_w,'XPTO')
order by coalesce(cd_material,0),
	coalesce(ie_tipo_material,'0'),
	coalesce(cd_grupo_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_classe_material,0),
	coalesce(nr_seq_familia,0),
	coalesce(cd_setor_atendimento,0),
	coalesce(cd_perfil,0);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	nr_seq_familia,
	ie_tipo_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	nr_seq_familia_w,
	ie_tipo_material_w
from	estrutura_material_v
where	cd_material		= cd_material_p;

select	max(cd_unidade_medida_solic)
into STRICT	cd_unid_med_solic_w
from	material
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_retorno_w	:= ie_retorno_w;
end loop;
close C01;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
