CREATE OR REPLACE FUNCTION obter_se_mostra_medic (nr_seq_medic_p bigint) RETURNS varchar AS $body$
DECLARE


ie_uso_continuo_w	varchar(1);
nr_dias_uso_w		integer;
dt_inicio_w		timestamp;
dt_suspensao_w		timestamp;
ie_retorno_w		varchar(1);


BEGIN

select	ie_uso_continuo,
	nr_dias_uso,
	dt_inicio,
	dt_suspensao
into STRICT	ie_uso_continuo_w,
	nr_dias_uso_w,
	dt_inicio_w,
	dt_suspensao_w
from	medic_uso_continuo
where	nr_sequencia	= nr_seq_medic_p;

if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
	ie_retorno_w	:= 'N';
elsif (ie_uso_continuo_w = 'S') then
	ie_retorno_w	:= 'S';
elsif (ie_uso_continuo_w = 'N') then

	dt_inicio_w	:= dt_inicio_w + nr_dias_uso_w;

	if (dt_inicio_w >= clock_timestamp()) then
		ie_retorno_w	:= 'S';
	else
		ie_retorno_w	:= 'N';
	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
