CREATE OR REPLACE FUNCTION obter_se_mostra_medida (cd_protocolo_p bigint, nr_seq_tipo_medida_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_laudo_medida
where	cd_protocolo	= cd_protocolo_p;

if (cont_w > 0) then

	select	count(*)
	into STRICT	cont_w
	from	regra_laudo_medida
	where	cd_protocolo	= cd_protocolo_p
	and	nr_seq_tipo_medida = nr_seq_tipo_medida_p;

	if (cont_w = 0) then
		ie_mostrar_w	:= 'N';
	end if;

end if;

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
