SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_nota_clinica (ds_tipo_p text, ie_tipo_nota_clinica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 			varchar(1);
nr_qtd_w				bigint;
nr_seq_config_w			bigint;
cd_pessoa_fisica_w  	varchar(10);
cd_perfil_w 			bigint;
cd_setor_atendimento_w 	bigint;
cd_estabelecimento_w 	bigint;

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	config_notas_clinicas
	where 	ie_tipo_nota_clinica = ie_tipo_nota_clinica_p
	and (ie_evolucao_clinica = ds_tipo_p or coalesce(ie_evolucao_clinica::text, '') = '');


BEGIN


cd_pessoa_fisica_w := obter_pf_usuario(wheb_usuario_pck.get_nm_usuario, 'C');
cd_perfil_w := wheb_usuario_pck.get_cd_perfil;
cd_setor_atendimento_w := wheb_usuario_pck.get_cd_setor_atendimento;
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;


select 	count(1)
into STRICT   	nr_qtd_w
from 	config_notas_clinicas;

if (nr_qtd_w > 0)then

	open C01;
	loop
	fetch C01 into
		nr_seq_config_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		ds_retorno_w := 'S';

		select  count(1)
		into STRICT   	nr_qtd_w
		from 	config_notas_clinicas_lib
		where 	nr_seq_config = nr_seq_config_w;

		if (nr_qtd_w > 0) then

			select 	max(coalesce('S','N'))
			into STRICT   	ds_retorno_w
			from 	config_notas_clinicas_lib
			where 	nr_seq_config = nr_seq_config_w
			and     coalesce(cd_pessoa_fisica,	  coalesce(cd_pessoa_fisica_w,0))	 = coalesce(cd_pessoa_fisica_w,0)
			and		coalesce(cd_perfil, 			  coalesce(cd_perfil_w,0)) 			 = coalesce(cd_perfil_w,0)
			and		coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w,0)) = coalesce(cd_setor_atendimento_w,0)
			and		coalesce(cd_estabelecimento,   coalesce(cd_estabelecimento_w,0))	 = coalesce(cd_estabelecimento_w,0);

			if (ds_retorno_w = 'S')then
				return ds_retorno_w;
			end if;

		end if;

	end;
	end loop;
	close C01;
else
	ds_retorno_w := 'S';
end if;


if (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
