CREATE OR REPLACE FUNCTION obter_se_mostra_ordem_usuario ( nr_ordem_compra_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_mostra_w			varchar(1) := 'N';
ie_possui_cc_local_w		varchar(1) := 'N';
cd_centro_custo_w			bigint;
cd_local_estoque_w		smallint;

c01 CURSOR FOR
SELECT	distinct
	a.cd_centro_custo,
	a.cd_local_estoque
from	ordem_compra_item a
where	a.nr_ordem_compra = nr_ordem_compra_p
and ((a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> '') or (a.cd_local_estoque IS NOT NULL AND a.cd_local_estoque::text <> ''));


BEGIN

open C01;
loop
fetch C01 into
	cd_centro_custo_w,
	cd_local_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_possui_cc_local_w	:= 'S';

	if (ie_mostra_w = 'N') then

		if (ie_mostra_w = 'N') and (cd_centro_custo_w IS NOT NULL AND cd_centro_custo_w::text <> '') and (obter_se_cc_lib_usuario(nm_usuario_p, cd_centro_custo_w) = 'S') then
			ie_mostra_w := 'S';
		end if;

		if (ie_mostra_w = 'N') and (cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') and (obter_se_local_lib_usuario(nm_usuario_p, cd_local_estoque_w) = 'S') then
			ie_mostra_w := 'S';
		end if;
	end if;
	end;
end loop;
close C01;

/*tratamento para mostrar a ordem, caso ela n√£o tenha nenhum centro de custo ou local de estoque. Ou seja, dai essa ordem pode mostrar*/

if (ie_possui_cc_local_w = 'N') then
	ie_mostra_w := 'S';
end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
