CREATE OR REPLACE FUNCTION obter_se_mostra_param_prod (cd_funcao_p text, nr_seq_param_p bigint, ie_produto_p text) RETURNS varchar AS $body$
DECLARE

ie_mostra_w			varchar(1) := 'S';

BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  -- Verifica se existe algum produto espefífico para o parâmetro da funcao
into STRICT	ie_mostra_w
from	funcao_param_prod a
where	a.cd_funcao = cd_funcao_p
and		a.nr_seq_param = nr_seq_param_p;

if (ie_mostra_w = 'N') then
	ie_mostra_w := 'S';
elsif (ie_mostra_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  -- Verifica se é o produto do parâmetro
	into STRICT	ie_mostra_w
	from	funcao_param_prod a
	where	a.cd_funcao = cd_funcao_p
	and		a.nr_seq_param = nr_seq_param_p
	and		a.ie_produto = ie_produto_p;
end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
