CREATE OR REPLACE FUNCTION obter_se_mostra_param ( cd_estabelecimento_p bigint, nm_usuario_p text, cd_perfil_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostra_w	varchar(1);
qt_registros_w	bigint;

/* Observacao
Os usuarios ou perfis cadastros na tabela nao poderao acessar os perfis das funcoes ou aplicacoes a que estiverem vinculados.
*/
BEGIN

-- Verificar se existe regra para restringir a visualizacao dos parametros
select	count(*)
into STRICT	qt_registros_w
from	regra_restringir_funcao f,
	regra_param_funcao p
where	p.nr_sequencia = f.nr_seq_regra_param;

-- Se nao houver regra, todos os parametros devem ser visualizados pelo usuario ou perfil. Caso exista, e preciso verificar se existe regra para a funcao
if (qt_registros_w = 0) then
	ie_mostra_w := 'S';
else
	-- Verifica se existe regra para a funcao
	select	count(*)
	into STRICT		qt_registros_w
	from		regra_restringir_funcao f,
				regra_param_funcao p
	where	p.nr_sequencia = f.nr_seq_regra_param
	and		f.cd_funcao = cd_funcao_p
	and		coalesce(p.nm_usuario_regra, nm_usuario_p) =  nm_usuario_p
	and		coalesce(p.cd_perfil, cd_perfil_p) = cd_perfil_p
	and 		coalesce(p.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;
	
	-- Caso nao exista regra para a funcao, os parametros podem ser visualizados
	if (qt_registros_w = 0) then
		ie_mostra_w := 'S';
	
	-- Caso exista regra para a funcao, e preciso verificar se a regra e para o usuario/perfil
	else
		ie_mostra_w	:= 'N';
	end if;
end if;

return	ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
