CREATE OR REPLACE FUNCTION obter_se_mostra_perfil (cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, ds_maquina_p text, ie_setor_usuario_p text, ie_comp_especifico_p text) RETURNS varchar AS $body$
DECLARE


ie_mostra_perfil_w			varchar(01)	:= 'S';
cd_setor_usuario_w			integer;
ie_nao_visualiza_comp_espec_w	varchar(01)	:= 'N';


BEGIN

select	coalesce(max(ie_nao_visualiza_comp_espec),'N')
into STRICT	ie_nao_visualiza_comp_espec_w
from	perfil
where	cd_perfil = cd_perfil_p;

if (ie_comp_especifico_p = 'S') and (ds_maquina_p IS NOT NULL AND ds_maquina_p::text <> '') then
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ie_mostra_perfil_w
	from	computador b,
		perfil_computador a
	where	a.nr_seq_computador	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nm_computador_pesquisa= padronizar_nome(upper(ds_maquina_p))
	and	a.cd_perfil		= cd_perfil_p
	and	b.cd_estabelecimento	= cd_estabelecimento_p;

	end;
end if;

if (coalesce(ie_nao_visualiza_comp_espec_w,'N') = 'S') and (ds_maquina_p IS NOT NULL AND ds_maquina_p::text <> '') then
	begin
	select	coalesce(max('N'),'S')
	into STRICT	ie_mostra_perfil_w
	from	computador b,
		perfil_computador a
	where	a.nr_seq_computador	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	b.nm_computador_pesquisa= padronizar_nome(upper(ds_maquina_p))
	and	a.cd_perfil		= cd_perfil_p
	and	b.cd_estabelecimento	= cd_estabelecimento_p;
	end;
end if;

if (ie_setor_usuario_p = 'S') and (ds_maquina_p IS NOT NULL AND ds_maquina_p::text <> '') then
	select	coalesce(max(cd_setor_atendimento),0)
	into STRICT	cd_setor_usuario_w
	from	usuario
	where	nm_usuario		= nm_usuario_p
	and	cd_estabelecimento		= cd_estabelecimento_p;
	if (cd_setor_usuario_w > 0) then
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_mostra_perfil_w
		from	computador a
		where	a.nm_computador_pesquisa= padronizar_nome(Upper(ds_maquina_p))
		and	a.ie_situacao		= 'A'
		and	a.cd_setor_atendimento	= cd_setor_usuario_w
		and	a.cd_estabelecimento	= cd_estabelecimento_p;

		end;
	end if;
end if;

return	ie_mostra_perfil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
