SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_mostra_prescr_val ( nr_prescricao_p bigint, nr_atendimento_p bigint, cd_setor_atend_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1) := 'N';
ie_alta_w		varchar(1) := 'N';
dt_validade_w	timestamp;
cd_setor_atendimento_w	setor_atendimento.cd_setor_atendimento%type;


BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(dt_validade_prescr) 
	into STRICT	dt_validade_w 
	from	prescr_medica 
	where	nr_prescricao = nr_prescricao_p;
	 
	select coalesce(max('S'), 'N') 
	into STRICT	ie_alta_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and		(dt_alta IS NOT NULL AND dt_alta::text <> '');
	 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = (SELECT Obter_Atepacu_paciente(nr_atendimento_p, 'A') );
	 
	 
	if (dt_validade_w < clock_timestamp()) or 
		((ie_alta_w = 'S') and (dt_validade_w <> clock_timestamp())) or 
		((cd_setor_atend_p <> cd_setor_atendimento_w) and (dt_validade_w <> clock_timestamp())) then 
		ie_retorno_w := 'S';
 
	end if;
 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
