CREATE OR REPLACE FUNCTION obter_se_mostra_retorno ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_ordem_w	bigint;
ie_retorno_w		varchar(1);


BEGIN

/* Pega o profissional e a ordem de atendimento */

select	obter_prof_agenda_multi(nr_seq_agenda_p, cd_estabelecimento_p, nm_usuario_p, 'O')
into STRICT	nr_seq_ordem_w
;

select	ie_solicita_retorno
into STRICT	ie_retorno_w
from	agenda_consulta_prof
where	nr_seq_ordem	= nr_seq_ordem_w
and	nr_seq_agenda	= nr_seq_agenda_p;

return	ie_retorno_w;

end;
