CREATE OR REPLACE FUNCTION obter_se_mostra_rotina (nr_seq_rotina_p bigint, cd_setor_p bigint, cd_perfil_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ie_mostrar_w	varchar(10) := 'S';
qt_itens_w	bigint;
				

BEGIN 
 
select	count(*) 
into STRICT	qt_itens_w 
from	REP_ROTINA_LIB 
where	nr_seq_rotina	= nr_seq_rotina_p;
 
if (qt_itens_w	> 0) then 
	select	count(*) 
	into STRICT	qt_itens_w 
	from	REP_ROTINA_LIB 
	where	nr_seq_rotina	= nr_seq_rotina_p 
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_p,0))	= coalesce(cd_setor_p,0) 
	and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0) 
	and	coalesce(cd_pessoa_fisica, coalesce(cd_pessoa_fisica_p,0)) = coalesce(cd_pessoa_fisica_p,0) 
	and	((coalesce(cd_especialidade::text, '') = '') or (coalesce(obter_se_especialidade_medico(cd_pessoa_fisica_p, cd_especialidade),'S') = 'S')) 
	and	((coalesce(nr_seq_equipe::text, '') = '') or (Obter_se_pf_equipe_atend(cd_pessoa_fisica_p, cd_pessoa_fisica_p) = 'S'));
	 
	if (qt_itens_w	= 0) then 
		ie_mostrar_w	:= 'N';
	end if;
end if;
 
return	ie_mostrar_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
