CREATE OR REPLACE FUNCTION obter_se_motalta_lib ( cd_funcao_p bigint, cd_motivo_p bigint, cd_convenio_p bigint default null, ie_tipo_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1)	:= 'S';
qt_reg_w		varchar(10);
cd_perfil_w		bigint;
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
	SELECT	ie_permite
	from	motivo_alta_liberacao
	where	coalesce(cd_funcao,coalesce(cd_funcao_p,0))	= coalesce(cd_funcao_p,0)
	and	cd_motivo_alta	= cd_motivo_p
	and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
	and	coalesce(cd_convenio,coalesce(cd_convenio_p,0))	= coalesce(cd_convenio_p,0)
	and	coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0)
	order by coalesce(cd_perfil,0),
		 coalesce(cd_convenio,0),
		 coalesce(ie_tipo_atendimento,0),
		 coalesce(cd_funcao,0);
	

BEGIN

cd_perfil_w		:= coalesce(obter_perfil_ativo,0);
cd_estabelecimento_w 	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

select	count(*)
into STRICT	qt_reg_w
from	motivo_alta_liberacao
where	coalesce(cd_funcao,cd_funcao_p)	= cd_funcao_p
and	cd_motivo_alta	= cd_motivo_p;

if (qt_reg_w	> 0) then
	ie_permite_w	:= 'S';
	open C01;
	loop
	fetch C01 into	
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
