CREATE OR REPLACE FUNCTION obter_se_motalta_usu ( cd_motivo_alta_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'S';
qt_existe_w	bigint;

BEGIN

if (coalesce(cd_motivo_alta_p,0) > 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

select 	count(*)
into STRICT	qt_existe_w
from	regra_motivo_alta_usuario
where	ie_situacao = 'A'
and	cd_motivo_alta = cd_motivo_alta_p;

	if (qt_existe_w > 0) then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	regra_motivo_alta_usuario
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	cd_motivo_alta = cd_motivo_alta_p
		and	ie_situacao 	 = 'A';

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
