SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_motalt_template ( cd_motivo_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1)	:= 'S';
qt_reg_w		varchar(10);
nr_seq_template_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_template
	from	motivo_alta_template
	where	cd_motivo_alta	= cd_motivo_p;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	motivo_alta_template
where	cd_motivo_alta	= cd_motivo_p;

if (qt_reg_w > 0) then
	ie_permite_w	:= 'N';

	open C01;
	loop
	fetch C01 into
		nr_seq_template_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		select	count(*)
		into STRICT	qt_reg_w
		from	ehr_registro a,
			ehr_reg_template b
		where	a.nr_sequencia = b.nr_seq_reg
		and	a.nr_atendimento = nr_atendimento_p
		and	b.nr_seq_template = nr_seq_template_w
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	coalesce(a.ie_situacao,'A') = 'A';

		if (qt_reg_w > 0) then
			ie_permite_w	:= 'S';
		end if;
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_motalt_template ( cd_motivo_p bigint, nr_atendimento_p bigint) FROM PUBLIC;

