CREATE OR REPLACE FUNCTION obter_se_motcancel_atend_regra (cd_perfil_p bigint, ie_tipo_atendimento_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_motivo_regra_w	integer;
ds_retorno_w		varchar(1);
qt_regra_w		integer;


BEGIN
select	count(*)
into STRICT	qt_regra_w
from	motivo_cancel_Atend_Regra
where	ie_tipo_atendimento	= ie_tipo_atendimento_p;

if (qt_regra_w > 0) then
	select	count(*)
	into STRICT	qt_motivo_regra_w
	from	motivo_cancel_atend_regra
	where	coalesce(cd_perfil, cd_perfil_p)			= cd_perfil_p
	and	coalesce(ie_tipo_atendimento, ie_tipo_Atendimento_p)	= ie_tipo_atendimento_p
	and	nr_seq_motivo_cancel				= nr_seq_motivo_cancel_p;

	if (qt_motivo_regra_w	> 0) then
		ds_retorno_w	:= 'N';
	else
		ds_retorno_w	:= 'S';
	end if;
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
