SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_motivo_atend_lib (nr_seq_queixa_p bigint, ie_clinica_p bigint default 0, nr_seq_classif_p bigint default 0) RETURNS varchar AS $body$
DECLARE


				
qt_reg_w				bigint;
qt_reg_cl_w				bigint;
cd_perfil_w				bigint	:= obter_perfil_ativo;
cd_setor_logado_w		bigint	:= wheb_usuario_pck.get_cd_setor_atendimento;

BEGIN
select	count(*)
into STRICT	qt_reg_w
from	QUEIXA_PACIENTE_LIB
where	nr_seq_queixa	= nr_seq_queixa_p;

qt_reg_cl_w := 0;

if (qt_reg_w = 0) then
	return 'S';
end if;

if (ie_clinica_p > 0) or (nr_seq_classif_p > 0) then
	select 	count(*)
	into STRICT	qt_reg_cl_w
	from 	QUEIXA_PACIENTE_LIB
	where	((coalesce(ie_clinica::text, '') = '') or (ie_clinica = ie_clinica_p))
	and		((coalesce(nr_seq_classif::text, '') = '') or (nr_seq_classif = nr_seq_classif_p))
	and    ((ie_clinica IS NOT NULL AND ie_clinica::text <> '') or (nr_seq_classif IS NOT NULL AND nr_seq_classif::text <> ''));
end if;

if (qt_reg_cl_w = 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	QUEIXA_PACIENTE_LIB
		where	nr_seq_queixa	= nr_seq_queixa_p
		and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
		and		coalesce(cd_setor_atendimento,cd_setor_logado_w)	= cd_setor_logado_w;
	
	if (qt_reg_w	= 0) then
		return 'N';
	end if;

elsif (qt_reg_cl_w > 0) then

	select	count(*)
	into STRICT	qt_reg_w
	from	QUEIXA_PACIENTE_LIB
	where	nr_seq_queixa	= nr_seq_queixa_p
	and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
	and		coalesce(cd_setor_atendimento,cd_setor_logado_w)	= cd_setor_logado_w
	and		((coalesce(ie_clinica::text, '') = '') or (ie_clinica = ie_clinica_p))
	and		((coalesce(nr_seq_classif::text, '') = '') or (nr_seq_classif = nr_seq_classif_p))
	and    ((ie_clinica IS NOT NULL AND ie_clinica::text <> '') or (nr_seq_classif IS NOT NULL AND nr_seq_classif::text <> ''));
	
	if (qt_reg_w	= 0) then
		return 'N';
	end if;
	
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
