CREATE OR REPLACE FUNCTION obter_se_motivo_baixa_conta (cd_motivo_baixa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_conta_paciente_w		varchar(1);


BEGIN

select	ie_conta_paciente
into STRICT	ie_conta_paciente_w
from	tipo_baixa_prescricao
where	cd_tipo_baixa		= cd_motivo_baixa_p
and	ie_prescricao_devolucao	= 'P';

RETURN ie_conta_paciente_w;

END;
$body$
