CREATE OR REPLACE FUNCTION obter_se_motivo_glosa_conv (cd_motivo_glosa_p bigint, nr_seq_item_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE



cd_convenio_w	bigint;
ie_permite_w	varchar(15) 	:= 'S';
qt_regra_w	bigint 	:= 0;
cd_perfil_w	integer 	:= obter_perfil_ativo;

BEGIN

cd_convenio_w := coalesce(cd_convenio_p,0);

if (coalesce(nr_seq_item_p,0) > 0) and (coalesce(cd_convenio_w,0) = 0) then

	select	coalesce(max(d.cd_convenio),0)
	into STRICT	cd_convenio_w
	from	lote_audit_hist_guia a,
		lote_audit_hist_item b,
		lote_audit_hist c,
		lote_auditoria d
	where	a.nr_sequencia 	= b.nr_seq_guia
	and	b.nr_sequencia 	= nr_seq_item_p
	and	c.nr_sequencia 	= a.nr_seq_lote_hist
	and	d.nr_sequencia  = c.nr_seq_lote_audit;

end if;

select	count(*)
into STRICT	qt_regra_w
from	MOTIVO_GLOSA_CONV a
where	a.cd_motivo_glosa = cd_motivo_glosa_p;

if (qt_regra_w > 0) then

	begin
	select	'S'
	into STRICT	ie_permite_w
	from	MOTIVO_GLOSA_CONV a
	where	a.cd_motivo_glosa 	= cd_motivo_glosa_p
	and	a.cd_convenio		= cd_convenio_w  LIMIT 1;
	exception
	when others then
		ie_permite_w := 'N';
	end;

end if;


return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
