CREATE OR REPLACE FUNCTION obter_se_motivo_glosa_estab ( cd_motivo_glosa_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_estab_adic_w		bigint;
cd_estabelecimento_w	smallint;


BEGIN

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	motivo_glosa a
where	a.cd_motivo_glosa	= cd_motivo_glosa_p;

if (cd_estabelecimento_w	= cd_estabelecimento_p) then

	ie_retorno_w	:= 'S';

else

	select	count(*)
	into STRICT	qt_estab_adic_w
	from	motivo_glosa_estab a
	where	a.cd_motivo_glosa	= cd_motivo_glosa_p;

	if (qt_estab_adic_w = 0) and (coalesce(cd_estabelecimento_w::text, '') = '') then

		ie_retorno_w	:= 'S';

	else

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	motivo_glosa_estab a
		where	a.cd_estabelecimento	= cd_estabelecimento_p
		and	a.cd_motivo_glosa	= cd_motivo_glosa_p;

	end if;

end if;

RETURN ie_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
