CREATE OR REPLACE FUNCTION obter_se_motivo_transf_alta ( cd_motivo_alta_p bigint, nr_seq_motivo_transf_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	motivo_transf_alta
where	cd_motivo_alta	= cd_motivo_alta_p;

if (cont_w > 0) then
	select	count(*)
	into STRICT	cont_w
	from	motivo_transf_alta
	where	cd_motivo_alta		= cd_motivo_alta_p
	and	nr_seq_motivo_transf	= nr_seq_motivo_transf_p;

	if (cont_w = 0) then
		ie_mostrar_w	:= 'N';
	end if;
end if;

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
