CREATE OR REPLACE FUNCTION obter_se_mudou_diag ( cd_pessoa_fisica_p text, cd_doenca_p text) RETURNS varchar AS $body$
DECLARE


qt_mudou_w	bigint;
ds_retorno_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_mudou_w
from	diagnostico_doenca_hist
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	cd_doenca		= cd_doenca_p;

if (qt_mudou_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
