CREATE OR REPLACE FUNCTION obter_se_mudou_preco_tipo (cd_laboratorio_p text, cd_apresentacao_p text, cd_medicamento_p text, ie_tipo_preco_p text, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
vl_medicamento_w	double precision;
vl_medic_aux_w	double precision := 0;

C01 CURSOR FOR
	SELECT	a.vl_preco_medicamento
	from	brasindice_preco	a
	where	a.cd_medicamento	= cd_medicamento_p
	and	a.cd_apresentacao	= cd_apresentacao_p
	and	a.cd_laboratorio	= cd_laboratorio_p
	and	a.ie_tipo_preco		= ie_tipo_preco_P
	and	trunc(a.dt_inicio_vigencia)	between dt_inicial_p and dt_final_p
	and	a.vl_preco_medicamento <> 0;

BEGIN

open C01;
loop	fetch C01 into
	vl_medicamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (vl_medic_aux_w <> 0)
		and (vl_medicamento_w <> vl_medic_aux_w) then
		ie_retorno_w:= 'S';
		exit;
	end if;
	vl_medic_aux_w := vl_medicamento_w;
	end;
end loop;
close C01;

return ie_retorno_w;

end;
$body$
