CREATE OR REPLACE FUNCTION obter_se_mudou_pr_simpro ( cd_simpro_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
vl_material_w	double precision;
vl_mat_aux_w	double precision := 0;
cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	a.vl_preco_venda
	from	simpro_preco a
	where	a.cd_simpro = cd_simpro_p
	and	coalesce(a.cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
	and	trunc(a.dt_vigencia) between dt_inicial_p and dt_final_p
	and	a.vl_preco_venda <> 0;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

open C01;
loop	fetch C01 into
	vl_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (vl_mat_aux_w <> 0)
		and (vl_material_w <> vl_mat_aux_w) then
		ie_retorno_w	:= 'S';
		exit;
	end if;
	vl_mat_aux_w	:= vl_material_w;
	end;
end loop;
close C01;

RETURN IE_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
