CREATE OR REPLACE FUNCTION obter_se_nao_conform_atrasada ( nr_seq_nao_conform_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	char(1) := 'N';

/*Function que verifica se a não conformidade possui alguma ação (ordem de serviço) que está em atraso*/

BEGIN

if (nr_seq_nao_conform_p IS NOT NULL AND nr_seq_nao_conform_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	man_ordem_servico where		(dt_conclusao_desejada IS NOT NULL AND dt_conclusao_desejada::text <> '')
	and		dt_conclusao_desejada < clock_timestamp()
	and		ie_status_ordem <> '3'
	and		nr_seq_nao_conform = nr_seq_nao_conform_p LIMIT 1;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
