CREATE OR REPLACE FUNCTION obter_se_nao_conform_vinculada ( nr_seq_nao_conform_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);


BEGIN
select	CASE WHEN sum(qt)=0 THEN  'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from (SELECT	count(1) qt
	from	qua_nao_conform_vinc b,
		qua_nao_conformidade x
	where	x.nr_sequencia = b.nr_seq_nao_conform_vinculada
	and	b.nr_seq_nao_conform = nr_seq_nao_conform_p 
	
union

	SELECT	count(1)
	from	qua_nao_conform_vinc b,
		qua_nao_conformidade x
	where	x.nr_sequencia = b.nr_seq_nao_conform
	and	b.nr_seq_nao_conform_vinculada = nr_seq_nao_conform_p  LIMIT 1) alias3;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
