CREATE OR REPLACE FUNCTION obter_se_nao_pbs_atendimento ( cd_material_p PRESCR_MATERIAL.CD_MATERIAL%type, nr_atendimento_p ATENDIMENTO_PACIENTE.NR_ATENDIMENTO%type ) RETURNS varchar AS $body$
DECLARE


ie_nopbs_w                  CONVENIO_PLANO.IE_EPS%type;


BEGIN

SELECT  CASE
            WHEN coalesce(MAX(c.IE_EPS), 'N') = 'S' AND coalesce(MAX(d.IE_NOPBS), 'N') = 'S' THEN
                'S' ELSE 'N'
            END IE_PBS
INTO STRICT    ie_nopbs_w            
FROM    MATERIAL_ATEND_PACIENTE a,
        ATEND_CATEGORIA_CONVENIO b,
        CONVENIO_PLANO c,
        MATERIAL d
WHERE   a.NR_ATENDIMENTO = b.NR_ATENDIMENTO
AND     b.CD_CONVENIO = c.CD_CONVENIO
AND     b.CD_PLANO_CONVENIO = c.CD_PLANO
AND     a.CD_MATERIAL = d.CD_MATERIAL
AND     a.NR_ATENDIMENTO = nr_atendimento_p
AND     a.CD_MATERIAL = cd_material_p;

RETURN  ie_nopbs_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
