SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_nascimento (nr_atendimento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*	0 =	S - Houve um Nascimento
		N - Não houve nascimento
		G - Nasceram Gêmeos

	1 =	Quantidade de Nascimentos	*/
qt_nascimento_w		integer;
ds_retorno_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_nascimento_w
from	nascimento
where	nr_atendimento	= nr_atendimento_p;

ds_retorno_w	:= to_char(qt_nascimento_w);

if (ie_opcao_p	= 0) then

	if (qt_nascimento_w = 0) then
		ds_retorno_w	:= 'N';
	elsif (qt_nascimento_w = 1) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'G';
	end if;
end if;


return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_nascimento (nr_atendimento_p bigint, ie_opcao_p bigint) FROM PUBLIC;

