CREATE OR REPLACE FUNCTION obter_se_nc_atualiz_periodo ( nr_sequencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
dt_atualizacao_w		timestamp;


BEGIN

begin
select	max(dt_atualizacao)
into STRICT	dt_atualizacao_w
from (SELECT	dt_atualizacao
	from	qua_nao_conformidade
	where	nr_sequencia = nr_sequencia_p
	
union

	SELECT	max(dt_atualizacao)
	from	qua_nao_conform_causa
	where	nr_seq_nao_conform = nr_sequencia_p
	
union

	select	max(dt_atualizacao)
	from	man_ordem_servico
	where	nr_seq_nao_conform = nr_sequencia_p
	
union

	select	max(b.dt_atualizacao)
	from	man_ordem_servico_exec b,
		man_ordem_servico a
	where	a.nr_sequencia = b.nr_seq_ordem
	and	a.nr_seq_nao_conform = nr_sequencia_p
	
union

	select	max(dt_atualizacao)
	from	qua_nao_conform_eficacia
	where	nr_seq_nao_conform = nr_sequencia_p) alias5;

select	'S'
into STRICT	ds_retorno_w

where	trunc(dt_atualizacao_w) between dt_inicial_p and dt_final_p;

exception
when others then
	ds_retorno_w := 'N';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
