CREATE OR REPLACE FUNCTION obter_se_negociado (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_ajuste_mat_w 	bigint;
ie_negociado_w			varchar(1);
ie_glosa_w			varchar(3);


BEGIN

ie_negociado_w:= 'N';	--Já Negociado
select 	coalesce(max(nr_seq_regra_ajuste_mat),0)
into STRICT	nr_seq_regra_ajuste_mat_w
from 	material_atend_paciente
where 	nr_sequencia = nr_sequencia_p;

if (coalesce(nr_seq_regra_ajuste_mat_w,0) > 0) then
	select 	coalesce(max(ie_glosa),'L')
	into STRICT	ie_glosa_w
	from 	regra_ajuste_material
	where 	nr_sequencia = nr_seq_regra_ajuste_mat_w
	and 	coalesce(ie_situacao,'I') = 'A';

	if (ie_glosa_w = 'X') then
		ie_negociado_w:= 'S'; -- Negociação
	end if;
end if;

return	ie_negociado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
