CREATE OR REPLACE FUNCTION obter_se_nf_ativa ( nr_seq_nf_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_situacao_w		varchar(1);


BEGIN

select	max(coalesce(ie_situacao,'9'))
into STRICT	ie_situacao_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_p;

if (ie_situacao_w <> '1') then
	ds_retorno_w	:= 'N';
else
	ds_retorno_w	:= 'S';
end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
