CREATE OR REPLACE FUNCTION obter_se_nf_inspecao_devolvida ( nr_seq_nota_p bigint, nr_seq_item_nf_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_devolvida_w		varchar(1) := 'N';
qt_existe_w		bigint := 0;


BEGIN

if (nr_seq_item_nf_p IS NOT NULL AND nr_seq_item_nf_p::text <> '') then
	select	count(*)
	into STRICT	qt_existe_w
	from	nota_fiscal x,
		nota_fiscal_item y,
		operacao_nota o
	where	x.cd_operacao_nf = o.cd_operacao_nf
	and	x.nr_Sequencia = y.nr_sequencia
	and	o.ie_devolucao = 'S'
	and	y.nr_item_nf = nr_seq_item_nf_p
	and (x.nr_sequencia_ref = nr_seq_nota_p or
		y.nr_seq_nf_orig = nr_seq_nota_p);
elsif (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	count(*)
	into STRICT	qt_existe_w
	from	nota_fiscal x,
		nota_fiscal_item y,
		operacao_nota o
	where	x.cd_operacao_nf = o.cd_operacao_nf
	and	x.nr_Sequencia = y.nr_sequencia
	and	o.ie_devolucao = 'S'
	and	y.cd_material = cd_material_p
	and (x.nr_sequencia_ref = nr_seq_nota_p or
		y.nr_seq_nf_orig = nr_seq_nota_p);
end if;

if (qt_existe_w > 0) then
	ie_devolvida_w := 'S';
else
	ie_devolvida_w := 'N';
end if;

return	ie_devolvida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
