CREATE OR REPLACE FUNCTION obter_se_nf_inutilizada (nr_nota_fiscal_p text, cd_serie_nf_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


qt_inutilizada_w	bigint := 0;
ds_retorno_w		varchar(1) := 'N';


BEGIN

select	count(1)
into STRICT	qt_inutilizada_w
from	nfe_inutilizacao
where	cd_cgc = cd_cgc_p
and	cd_serie_nf = cd_serie_nf_p
and	nr_inicial <= (somente_numero(nr_nota_fiscal_p))::numeric
and	nr_final >=  (somente_numero(nr_nota_fiscal_p))::numeric 
and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_inutilizada_w > 0) then
	begin
	ds_retorno_w := 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
