CREATE OR REPLACE FUNCTION obter_se_nf_lic (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w		varchar(1);


BEGIN

select	count(*)
into STRICT	cont_w
from	(SELECT	1
	from	ordem_compra_item x,
		nota_fiscal_item z
	where	x.nr_ordem_compra 	= z.nr_ordem_compra
	and	x.nr_item_oci		= z.nr_item_oci
	and	((coalesce(x.nr_seq_lic_item,0) <> 0) or (coalesce(nr_seq_reg_lic_item,0) <> 0))
	and	z.nr_sequencia		= nr_sequencia_p
	
union

	SELECT	1
	from	nota_fiscal_item x
	where	x.nr_sequencia		= nr_sequencia_p
	and	coalesce(x.nr_seq_lic_item,0) <> 0) alias7;


if (cont_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
